mapper.update:
UPDATE ms_class
teacher_id = #{teacherId}, headmaster_id = #{headmasterId}, class_name = #{clazzName}, class_cover = #{clazzCover}, start_time = #{startTime}, finish_time = #{finishTime}, finish = #{finish}, update_time = #{updateTime}, update_by = #{updateBy}, remark = #{remark}
class_id = #{clazzId}

mapper.select:
SELECT catalogue_name, play_url, courseware_url, catalogue_sort, last
FROM ms_catalogue
catalogue_id = #{catalogueId}

mapper.update:
UPDATE ms_catalogue
catalogue_name = #{catalogueName}, play_name = #{playName}, play_url = #{playUrl}, play_length = #{playLength}, courseware_name = #{coursewareName}, courseware_url = #{coursewareUrl}, catalogue_sort = #{catalogueSort}, last = #{last}, update_by = #{updateBy}, update_time = #{updateTime}
catalogue_id = #{catalogueId}
