mapper.update:
UPDATE ms_student
birthday = #{birthday}, grade = #{grade}, school = #{school}, major = #{major}, direction = #{direction}, graduation = #{graduation}, worked = #{worked}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}
user_id = #{userId}

UPDATE sys_user
user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}
user_id = #{userId}

mapper.select:
SELECT su.nick_name, mei.city, mei.company, mei.salary, mei.position, mei.remark
FROM sys_user AS su,
     ms_employment_info AS mei
WHERE su.user_id = #{userId}
  AND mei.user_id = #{userId}

mapper.update:
UPDATE ms_employment_info
city = #{city}, company = #{company}, salary = #{salary}, position = #{position}, remark = #{remark}
user_id = #{userId}
