mapper.select:
SELECT class_name
FROM ms_class
WHERE class_id = #{clazzId}

mapper.update:
UPDATE ms_student
birthday = #{birthday}, grade = #{grade}, school = #{school}, major = #{major}, direction = #{direction}, graduation = #{graduation}, worked = #{worked}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}
user_id = #{userId}

UPDATE sys_user
user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}
user_id = #{userId}
